
import unittest
from rfun.evaluation import AtPrecisionEvaluator, MultiParameterEvaluator
import json

class Test(unittest.TestCase):


    def setUp(self):
        pass


    def tearDown(self):
        pass


    def test_atP(self):
        e = AtPrecisionEvaluator([10, 25, 300], range(300), range(300))
        assert e.score() == 1.0
        
        # expecting 1.0
        e = AtPrecisionEvaluator([10, 25, 300], range(300), range(10))
        self.assertAlmostEqual(e.score(), 1.0, delta=0.00005)
        
        # expecting 1.0
        e = AtPrecisionEvaluator([10, 25, 300], range(300), range(100))
        self.assertAlmostEqual(e.score(), 1.0, delta=0.00005)
        
        # expecting < 1.0
        e = AtPrecisionEvaluator([10, 25, 300], range(10), range(300))
        self.assertAlmostEqual(e.score(), 0.9812727407737433, delta=0.00005)
        
        # expecting < 1.0 and > 0.96
        e = AtPrecisionEvaluator([10, 25, 300], range(100), range(300))
        self.assertAlmostEqual(e.score(), 0.9985148882453404, delta=0.00005)

        
        # if we cared about order, this would be much lower score ~0.00222
        e = AtPrecisionEvaluator([10, 25, 300], range(300), list(reversed(range(300))))
        self.assertAlmostEqual(e.score(), 1.0, delta=0.00005)

        e = AtPrecisionEvaluator([10, 25, 300], range(300), [0] * 10 + range(300))
        self.assertAlmostEqual(e.score(), 0.11574218459939112, delta=0.00005)

        e = AtPrecisionEvaluator([10, 25, 300], range(300), [0] * 5 + range(300))
        self.assertAlmostEqual(e.score(), 0.5093636296131284, delta=0.00005)
        
        
        e = AtPrecisionEvaluator([25, 50, 300], range(300), [0] * 5 + range(300))
        self.assertAlmostEqual(e.score(), 0.8074074074074074, delta=0.00005)
        
    
    def test_evaluator(self):
        gold_set = ['14062219', '14024620']
        docs = enhanced['response']['docs']
        se = MultiParameterEvaluator([2, 5, 100],
                                    docs, 
                                     gold_set, 
                                     [0.1, 1, 0.1], 
                                     [0.1, 1, 0.1], 
                                     [20, 50, 10], 
                                     normalizeWeight=True,
                                     fieldBoost=None)
        num_runs, score, params = list(se.run())[0]
        
        assert num_runs == 200
        assert score >= 0.0
        assert 'k1' in params
        


